      inliner.on('progress', function progressEvent(event) {
        progress = event;
        // console.log(JSON.stringify({ type: 'progress', progress, jobs }));
        update(event, jobs, argv.debug);
      });

      inliner.on('jobs', function jobsEvent(event) {
        jobs = event;
        // console.log(JSON.stringify({ type: 'jobs', progress, jobs }));
        update(progress, jobs, argv.debug);
      });

      inliner.on('warning', function warningEvent(event) {
        progress = event;
        // console.log(JSON.stringify({ type: 'warning', progress, jobs }));
        update(event, jobs, true);
      });

      inliner.on('end', function () {
        update.end(time);
      });

      'exit SIGINT SIGTERM'.split(' ').map(function (event) {
        process.once(event, function () {
          process.stderr.write(ansi.cursorShow); // put the cursor back
          try { process.kill(process.pid, event); } catch (e) {}
        });
      });
    } else {
      inliner.on('warning', function progress(event) {
        console.warn('warning: ' + event);
      });
    }
  }).catch(function (error) {
    console.error(error.stack);
  });
}

module.exports = options;

var minimist = require('minimist');

function options(args) {
  var argv = minimist(args, {
    boolean: [ // flags
      'version',
      'help',
      'debug',
      'verbose',
      'noimages',
      'nocompress',
      'nosvg',
      'skip-absolute-urls',
      'videos',
      'inlinemin',
      'preserve-comments',
      'iesafe',
    ],
    string: [ // options
      'encoding',
    ],
    alias: {
      images: 'noimages', // legacy support
      V: 'version',
      h: 'help',
      d: 'debug',
      v: 'verbose',
      i: 'noimages',
      n: 'nocompress',
      e: 'encoding',
      s: 'nosvg',
      o: 'videos',
      m: 'inlinemin',
      H: 'header',
    },
  });

  // copy across specific options
  if (argv.nocompress) {
    argv.compressCSS = false;
    argv.compressJS = false;
    argv.collapseWhitespace = false;
  }
  if (argv['preserve-comments']) {
    argv.preserveComments = true;
  }

  if (argv['skip-absolute-urls']) {
    argv.skipAbsoluteUrls = true;
  }
  argv.images = !argv.noimages;

  argv.useStdin = !process.stdin.isTTY;

  argv.verbose = argv.verbose || !process.stdout.isTTY;

  return argv;
}

var styles = require('ansi-styles');
var ansi = require('ansi-escapes');

console.warn(ansi.cursorHide + '\n\n' + ansi.cursorUp() +
        ansi.cursorSavePosition);

module.exports = function (progress, jobs, keep) {
  if (keep) {
    process.stderr.write(
      ansi.cursorRestorePosition +
      ansi.cursorLeft +
      ansi.eraseLines(2) +
      styles.red.open + ' ' + styles.red.close +
      styles.gray.open + progress + styles.gray.close +
      '\n\n' +
      ansi.cursorSavePosition +
      '\n\n'
    );
  }

  var remaining = jobs.breakdown.join(', ');
  if (remaining) {
    remaining = ' remaining: ' + remaining;
  }

  var t = jobs.todo / jobs.total * 100;
  if (t < 1) {
    t = 0;
  } else {
    t = t | 0;
  }

  var str = styles.green.open +
    (100 - t) + '%' +
    styles.green.close +
    remaining +
    styles.gray.open +
    '\nLast job: ' + progress +
    styles.gray.close;

  process.stderr.write(
    ansi.cursorRestorePosition +
    ansi.cursorLeft +
    ansi.eraseLines(2) +
    str.trim() + '\n');
};

module.exports.end = function (time) {
  var diff = process.hrtime(time);
  process.stderr.write(styles.green.open + 'Time: ' + diff[0] + 's ' +
    (diff[1] / 1e6).toFixed(3) + 'ms\n' + styles.green.close);
  process.stderr.write(ansi.cursorShow);
};

